import React from 'react';
import Lottie from 'react-lottie';
import animationData from '../../Assests/animations/animation_lne5kapr.json';

const Loader = () => {
  const defaultOptions = {
    loop: false,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice',
      background: '#F6F6F5'
    }
  };

  const modifyAnimationData = (data) => {
    const modifiedData = JSON.parse(JSON.stringify(data));

    modifiedData.layers.forEach(layer => {
      if (layer.shapes) {
        layer.shapes.forEach(shape => {
          if (shape.it) {
            shape.it.forEach(item => {
              if (item.c) {
                item.c.k = [14, 116, 144]; // Màu mới (ví dụ: màu đỏ)
              }
            });
          }
        });
      }
    });

    return modifiedData;
  };

  const modifiedAnimationData = modifyAnimationData(animationData);

  return (
    <div className="w-full h-screen flex items-center justify-center">
      <Lottie
        options={{ ...defaultOptions, animationData: modifiedAnimationData }}
        width={300}
        height={300}
      />
    </div>
  );
};

export default Loader;
