import React, { useEffect, useState } from "react";
const moment = require('moment');

const CountDown = (data) => {
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
    return () => clearTimeout(timer);
  });

  function calculateTimeLeft() {
    const finishDate = moment(data.Finish_Date, 'MM/DD/YYYY');
    const currentDate = moment();
    const difference = finishDate.diff(currentDate);
  
    console.log(difference);
  
    let timeLeft = {};
  
    if (difference > 0) {
      timeLeft = {
        days: finishDate.diff(currentDate, 'days'),
        hours: finishDate.diff(currentDate, 'hours') % 24,
        minutes: finishDate.diff(currentDate, 'minutes') % 60,
        seconds: finishDate.diff(currentDate, 'seconds') % 60,
      };
    }
  
    return timeLeft;
  }

  const timerComponents = Object.keys(timeLeft).map((interval) => {
    if (!timeLeft[interval]) {
      return null;
    }

    return (
      <span className="text-[25px] text-[#475ad2]">
        {timeLeft[interval]} {interval}{" "}
      </span>
    );
  });

  return (
    <div>
      {timerComponents.length ? (
        timerComponents
      ) : (
        <span className="text-[red] text-[25px]">Time's up</span>
      )}
    </div>
  );
};

export default CountDown;
