import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "../../../styles/styles";
import { AiFillHeart, AiFillStar, AiOutlineEye, AiOutlineHeart, AiOutlineShoppingCart, AiOutlineStar } from "react-icons/ai";
import ProductDetailsCard from "../ProductDetailsCard/ProductDetailsCard"
import { backend_url } from "../../../server";
import { useDispatch, useSelector } from "react-redux"
import { addToWishlist, removeFromWishlist } from "../../../redux/actions/wishlist";

const ProductCard = ({ data }) => {
  
  const [click, setClick] = useState(false);
  const [open, setOpen] = useState(false);
  const dispatch = useDispatch();
  const d = data.name;
  const product_name = d.replace(/\s+/g, "-");

  const removeFromWishlistHandler = (data) => {
    setClick(!click);
    dispatch(removeFromWishlist(data));
  };

  const addToWishlistHandler = (data) => {
    setClick(!click);
    dispatch(addToWishlist(data));
  }

  return (
    <>
      <div className="w-full h-[370px] bg-white rounded-lg shadow-sm p-3 relative cursor-pointer">
        <div className="flex justify-end"></div>
        <Link to={`/product/${product_name}`}>
          <img
            src={`${backend_url}${data.images && data.images[0]?.url}`}
            alt=""
            className="w-full h-[170px] object-contain"
          />
        </Link>
        <Link to="/">
          <h5 className={`${styles.shop_name}`}>{data.shop.name}</h5>
        </Link>
        <Link to={`/product/${data._id}`}>
          <h4 className="pb-3 font-[500]">
            {data.name.length > 40 ? data.name.slice(0, 40) + "..." : data.name}
          </h4>

          <div className="flex">
            <AiFillStar
            className="mr-2 cursor-pointer"
            size={20}
            color="#F6BA00"
            />
            <AiFillStar
            className="mr-2 cursor-pointer"
            size={20}
            color="#F6BA00"
            />
            <AiFillStar
            className="mr-2 cursor-pointer"
            size={20}
            color="#F6BA00"
            />
            <AiFillStar
            className="mr-2 cursor-pointer"
            size={20}
            color="#F6BA00"
            />
            <AiOutlineStar
            className="mr-2 cursor-pointer"
            size={20}
            color="#F6BA00"
            />
          </div>

          <div className="py-2 flex items-center justify-between">
            <div className="flex">
              <h5 className={`${styles.productDiscountPrice}`}>
                {data.originalPrice === 0
                  ? data.originalPrice
                  : data.discountPrice}
                $
              </h5>
              <h4 className={`${styles.price}`}>
                {data.originalPrice ? data.originalPrice + " $" : null}
              </h4>
            </div>
            <span className="font-[400] text-[17px] text-[#68d284]">
              {data?.sold_out} sold
            </span>
          </div>
        </Link>
          {/* Side options */}
          <div>
            {click ? (
                <AiFillHeart
                size={22}
                className="cursor_pointer absolute right-2 top-5"
                onClick={() => removeFromWishlistHandler(data)}
                color={click ? "red" : "#333"}
                title="Remove from wishlist"
                />
            ) : (
                <AiOutlineHeart size={22}
                className="cursor_pointer absolute right-2 top-5"
                onClick={() => addToWishlistHandler(data)}
                color={click ? "red" : "#333"}
                title="Add from wishlist"
                />
            )}
            <AiOutlineEye
                size={22}
                className="cursor_pointer absolute right-2 top-12"
                onClick={() => setOpen(!open)}
                color="#333"
                title="Quick view"
            />
            <AiOutlineShoppingCart
                 size={25}
                 className="cursor_pointer absolute right-2 top-20"
                 onClick={() => setOpen(!open)}
                 color="#444"
                 title="Add to cart"
            />
            {
              open ? 
                <ProductDetailsCard setOpen={setOpen} data={data} />
              : null
            }
          </div>
      </div>
    </>
  );
};

export default ProductCard;
