import React, { useEffect, useState } from "react";

const CountDown = ({ data }) => {
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
    return () => clearTimeout(timer);
  });

  function convertDateFormat(inputDate) {
    if (inputDate) {
      const dateObject = new Date(inputDate);
      const month = (dateObject.getMonth() + 1).toString().padStart(2, '0'); // Thêm '0' nếu cần
      const day = dateObject.getDate().toString().padStart(2, '0'); // Thêm '0' nếu cần
      const year = dateObject.getFullYear();  
      return `${month}-${day}-${year}`;
    } else {
      return null;
    }
  }
  
  function calculateTimeLeft() {
    // Kiểm tra xem data tồn tại và có thuộc tính Finish_Date không
    if (data && data.Finish_Date) {

      // Chuyển đổi định dạng của Finish_Date
      const formattedFinishDate = convertDateFormat(data.Finish_Date);
      const difference = +new Date(formattedFinishDate) - +new Date();

      let timeLeft = {};

      if (difference > 0) {
        timeLeft = {
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor(
            (difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
          ),
          minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((difference % (1000 * 60)) / 1000),
        };
      }

      return timeLeft;
    } else {
      return null;
    }
  }

  const timerComponents =
    timeLeft && Object.keys(timeLeft).length
      ? Object.keys(timeLeft).map((interval) => {
          if (!timeLeft[interval]) {
            return null;
          }

          return (
            <span key={interval} className="text-[25px] text-[#475ad2]">
              {timeLeft[interval]} {interval}{" "}
            </span>
          );
        })
      : null;

  return (
    <div>
      {timerComponents ? (
        timerComponents
      ) : (
        <span className="text-[red] text-[25px]">Time's up</span>
      )}
    </div>
  );
};

export default CountDown;
